import React,{Component} from 'react';
import './sidebar.css'

class sidebar extends Component{
    state={
        active:false
    }
    render(){
        console.log(this.state.active);
    return (
        <div className={this.state.active?"sidebar-is-active":""}>
        <div className={this.state.active?"sidebar sidebar-is-active":"sidebar"} >
  <div className={this.state.active?"navigation sidebar-is-active":"navigation"}>
    <nav className={this.state.active?"sidebar-is-active":""}>
      <ul className={this.state.active?"sidebar-is-active":""}>
        <li className={this.state.active?"sidebar-is-active":""}><a href="#section1">Sektion 1</a></li>
        <li className={this.state.active?"sidebar-is-active":""}><a href="#section2">Sektion 2</a></li>
        <li className={this.state.active?"sidebar-is-active":""}><a href="#section3">Sektion 3</a></li>
        <li className={this.state.active?"sidebar-is-active":""}><a href="#section4">Sektion 4</a></li>
        <li className={this.state.active?"sidebar-is-active":""}><a href="#section5">Sektion 5</a></li>
      </ul>
    </nav>
  </div>
</div>

<div className={this.state.active?"navigation-trigger sidebar-is-active":"navigation-trigger"} onClick={()=>{
            this.state.active?this.setState({active:false}):this.setState({active:true})
        }}>
  <span className={this.state.active?"line line-1 sidebar-is-active":"line line-1"}></span>
  <span className={this.state.active?"line line-2 sidebar-is-active":"line line-2"}></span>
</div>
</div>
    )}
}

export default sidebar
