import React,{Component} from 'react';
import './sidebar.css'

class sidebar extends Component{
    state={
        active:false
    }
    render(){
        console.log(this.state.active);
    return (
        <div className={this.state.active?"sidebar-is-active":""}>
        <div class="sidebar" >
  <div class="navigation" onClick={()=>{
            this.useState({active:true});
        }}>
    <nav>
      <ul>
        <li><a href="#section1">Sektion 1</a></li>
        <li><a href="#section2">Sektion 2</a></li>
        <li><a href="#section3">Sektion 3</a></li>
        <li><a href="#section4">Sektion 4</a></li>
        <li><a href="#section5">Sektion 5</a></li>
      </ul>
    </nav>
  </div>
</div>

<div class="navigation-trigger">
  <span class="line line-1"></span>
  <span class="line line-2"></span>
</div>
</div>
    )}
}

export default sidebar
