import React,{Component} from 'react';
import { render } from 'react-dom';
import './sidebar.css'
class sidebar extends Component {
    state={
        button:"button",
        main:"main",
        item:"sidebar-item",
        active:0
    }

    toggleSidebar=()=>{
        if(this.state.active===0){
            this.setState({
                button:"button active",
                main:"main move-to-left",
                item:"sidebar-item active",
                active:1
            })
        }
        else{
            this.setState({
                button:"button",
                main:"main",
                item:"sidebar-item",
                active:0
            })
        }
    }

    render(){
            return(
            <div>
                <div class="nav-right visible-xs">
                <div class={this.state.button} id="btn" >
                <div class="bar top"></div>
                <div class="bar middle"></div>
                <div class="bar bottom"></div>
                </div>
                </div>
                <main className={this.state.main}>
                <nav>
                    <div class="nav-right hidden-xs">
                    <div class={this.state.button} id="btn" onClick={this.toggleSidebar()}>
                        <div class="bar top"></div>
                        <div class="bar middle"></div>
                        <div class="bar bottom"></div>
                    </div>
                    </div>
                </nav>
                </main>

            <div class="sidebar">
                <ul class="sidebar-list">
                    <li class={this.state.item}><a href="#" class="sidebar-anchor">Item 1</a></li>
                    <li class={this.state.item}><a href="#" class="sidebar-anchor">Item 2</a></li>
                    <li class={this.state.item}><a href="#" class="sidebar-anchor">Item 3</a></li>
                    <li class={this.state.item}><a href="#" class="sidebar-anchor">Item 4</a></li>
                </ul>
                </div>
            </div>
        )}
    };
export default sidebar;
